package com.example.dataaccess.mapper;

import com.example.dataaccess.entity.CreditHistory;
import com.example.domain.valueobject.Money;
import com.example.domain.valueobject.OrderId;
import org.springframework.stereotype.Component;

@Component
public class CreditHistoryDataMapper {
    public CreditHistory toEntity(com.example.domain.entity.CreditHistory domain) {
        CreditHistory entity = new CreditHistory();
        entity.setId(domain.getId());
        entity.setCustomerId(domain.getCustomerId() == null ? null : domain.getCustomerId().getValue());
        entity.setAmount(domain.getAmount() == null ? null : domain.getAmount().getAmount());
        entity.setTransactionType(domain.getTransactionType());
        entity.setCreatedAt(domain.getCreatedAt());
        return entity;
    }

    public com.example.domain.entity.CreditHistory toDomain(CreditHistory entity) {
        OrderId cid = entity.getCustomerId() == null ? null : new OrderId(entity.getCustomerId());
        Money money = entity.getAmount() == null ? new Money((java.math.BigDecimal) null) : new Money(entity.getAmount());
        return new com.example.domain.entity.CreditHistory(entity.getId(), cid, money, entity.getTransactionType(), entity.getCreatedAt());
    }
}

