package com.example.dataaccess.mapper;

import com.example.dataaccess.entity.PaymentEnity;
import com.example.domain.entity.Payment;
import com.example.domain.valueobject.CustomerId;
import com.example.domain.valueobject.Money;
import com.example.domain.valueobject.OrderId;
import com.example.domain.valueobject.PaymentStatus;
import org.springframework.stereotype.Component;

@Component
public class PaymentDataMapper {
    public PaymentEnity toEntity(Payment payment) {
        PaymentEnity entity = new PaymentEnity();
        entity.setId(payment.getId());
        entity.setOrderId(payment.getOrderId() == null ? null : payment.getOrderId().getValue());
        entity.setPrice(payment.getPrice() == null ? null : payment.getPrice().getAmount());
        entity.setStatus(payment.getStatus());
        entity.setCustomerId(payment.getCustomerId() == null ? null : payment.getCustomerId().getValue());
        entity.setCreatedAt(payment.getCreatedAt());
        return entity;
    }

    public Payment toDomain(PaymentEnity entity) {
        Payment payment = new Payment(
                new OrderId(entity.getOrderId()),
                new CustomerId(entity.getCustomerId()),
                new Money(entity.getPrice()),
                entity.getStatus(),
                entity.getCreatedAt());

        payment.setId(entity.getId());
        return payment;
    }
}

